package com.example.soccerstore

import android.os.Bundle
import android.widget.Toast
import androidx.appcompat.app.AlertDialog
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.chip.Chip
import com.google.android.material.chip.ChipGroup
import com.google.android.material.floatingactionbutton.FloatingActionButton
import com.google.android.material.textfield.TextInputEditText
import androidx.core.widget.addTextChangedListener
import java.text.NumberFormat
import java.util.Locale

class MainActivity : AppCompatActivity() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var adapter: ProductAdapter
    private lateinit var chipGroup: ChipGroup
    private lateinit var searchInput: TextInputEditText
    private lateinit var cartFab: FloatingActionButton

    private val cart = mutableListOf<CartItem>()
    private var allProducts = listOf<Product>()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        initViews()
        setupProducts()
        setupRecyclerView()
        setupFilters()
        setupSearch()
        setupCart()
    }

    private fun initViews() {
        recyclerView = findViewById(R.id.recyclerView)
        chipGroup = findViewById(R.id.chipGroup)
        searchInput = findViewById(R.id.searchInput)
        cartFab = findViewById(R.id.cartFab)
    }

    private fun setupProducts() {
        allProducts = listOf(
            Product(1, "프로 축구화 - 스피드 X", "shoes", 189000,
                "data:image/jpeg;base64,",
                "경량 디자인의 프로급 축구화"),
            Product(2, "공식 경기용 축구공", "ball", 45000,
                "data:image/jpeg;base64,",
                    "FIFA 공인 5호 축구공"),
            Product(3, "유니폼 (상의)", "uniform", 89000,
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRRwhHRXEA2NtBNy_Klv4W5lezn4sVdLGKS6I1krmb7FDzJKK89IZqn3K_kpa6ObedXhCg&usqp=CAU",
                "통기성 좋은 프로 유니폼"),
            Product(4, "축구 트레이닝화", "shoes", 129000,
                "data:image/jpeg;base64,",
                "실내외겸용 트레이닝화"),
            Product(5, "골키퍼 글러브", "gear", 65000,
                "data:image/jpeg;base64,",
                "프로급 그립력의 골키퍼 장갑"),
            Product(6, "정강이 보호대", "gear", 25000,
                "https://static.nike.com/a/images/t_web_pdp_936_v2/f_auto/951da2a0-e01d-47cd-b709-ef82f853b053/NK+J+GUARD.png",
                "충격 흡수 보호대"),
            Product(7, "트레이닝 콘 세트", "training", 35000,
                "data:image/jpeg;base64,",
                "20개입 훈련용 콘"),
            Product(8, "축구 가방", "bag", 55000,
                "https://item.elandrs.com/r/image/oa/2024-02-15/35b9e123-27f1-40ed-95cb-8873ad16acdb.jpg?w=750&h=&q=100",
                "대용량 스포츠 백")
        )
    }

    private fun setupRecyclerView() {
        adapter = ProductAdapter(allProducts) { product ->
            addToCart(product)
        }

        recyclerView.layoutManager = GridLayoutManager(this, 2)
        recyclerView.adapter = adapter
    }

    private fun setupFilters() {
        chipGroup.setOnCheckedStateChangeListener { _, checkedIds ->
            if (checkedIds.isEmpty()) {
                filterProducts("all", searchInput.text.toString())
                return@setOnCheckedStateChangeListener
            }

            val chip = findViewById<Chip>(checkedIds[0])
            val category = when (chip.text.toString()) {
                "축구화" -> "shoes"
                "축구공" -> "ball"
                "유니폼" -> "uniform"
                "장비" -> "gear"
                "훈련용품" -> "training"
                "가방" -> "bag"
                else -> "all"
            }
            filterProducts(category, searchInput.text.toString())
        }
    }

    private fun setupSearch() {
        searchInput.addTextChangedListener { text ->
            val selectedChipId = chipGroup.checkedChipId
            val category = if (selectedChipId != -1) {
                val chip = findViewById<Chip>(selectedChipId)
                when (chip.text.toString()) {
                    "축구화" -> "shoes"
                    "축구공" -> "ball"
                    "유니폼" -> "uniform"
                    "장비" -> "gear"
                    "훈련용품" -> "training"
                    "가방" -> "bag"
                    else -> "all"
                }
            } else "all"

            filterProducts(category, text.toString())
        }
    }

    private fun filterProducts(category: String, searchText: String) {
        var filtered = if (category == "all") {
            allProducts
        } else {
            allProducts.filter { it.category == category }
        }

        if (searchText.isNotEmpty()) {
            filtered = filtered.filter {
                it.name.contains(searchText, ignoreCase = true) ||
                        it.description.contains(searchText, ignoreCase = true)
            }
        }

        adapter.updateProducts(filtered)
    }

    private fun addToCart(product: Product) {
        val existingItem = cart.find { it.product.id == product.id }

        if (existingItem != null) {
            existingItem.quantity++
        } else {
            cart.add(CartItem(product, 1))
        }

        updateCartBadge()
        Toast.makeText(this, "${product.name}이(가) 장바구니에 추가되었습니다", Toast.LENGTH_SHORT).show()
    }

    private fun setupCart() {
        cartFab.setOnClickListener {
            showCartDialog()
        }
    }

    private fun updateCartBadge() {
        val totalItems = cart.sumOf { it.quantity }
        cartFab.contentDescription = "장바구니 ($totalItems)"
    }

    private fun showCartDialog() {
        if (cart.isEmpty()) {
            Toast.makeText(this, "장바구니가 비어있습니다", Toast.LENGTH_SHORT).show()
            return
        }

        val items = cart.joinToString("\n") {
            "${it.product.name} x${it.quantity} - ${formatPrice(it.product.price * it.quantity)}"
        }
        val total = cart.sumOf { it.product.price * it.quantity }

        AlertDialog.Builder(this)
            .setTitle("장바구니")
            .setMessage("$items\n\n총액: ${formatPrice(total)}")
            .setPositiveButton("주문하기") { _, _ ->
                Toast.makeText(this, "주문이 완료되었습니다!", Toast.LENGTH_LONG).show()
                cart.clear()
                updateCartBadge()
            }
            .setNegativeButton("취소", null)
            .setNeutralButton("비우기") { _, _ ->
                cart.clear()
                updateCartBadge()
                Toast.makeText(this, "장바구니를 비웠습니다", Toast.LENGTH_SHORT).show()
            }
            .show()
    }

    private fun formatPrice(price: Int): String {
        val formatter = NumberFormat.getNumberInstance(Locale.KOREA)
        return "${formatter.format(price)}원"
    }
}